tinymce.PluginManager.requireLangPack('facebookembed', 'en_EN');
tinymce.PluginManager.add('facebookembed', function (editor, url) {

    function openmanager() {
        editor.windowManager.open({
            file: url + '/dialog.html?r=' + Math.random(),
            width: 650,
            height: 500,
            inline: 1,
            title: 'Embed Facebook'
        }, {
            plugin_url: url // Plugin absolute URL
        });


    }

    editor.addButton('facebookembed', {
        text: 'Embed Facebook',
        icon: true,
        image: tinyMCE.baseURL + '/plugins/facebookembed/img/facebook.png',
        tooltip: 'Add facebook post',
        onclick: openmanager
    });

    editor.addMenuItem('facebookembed', {
        icon: 'media',
        text: 'Add facebppk post',
        onclick: openmanager,
        context: 'insert'
    });
});
